import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import OMDB_URL from "../helpers/OmbdUrl";

const Detailts = () => {
  const params = useParams();
  const [movie, setMovie] = useState();

  //prendere la key 2491cb2f

  const getOmdbData = () => {
    fetch(OMDB_URL + "&i=" + params.omdbId)
      .then((resp) => {
        if (resp.ok) {
          return resp.json();
        }
      })
      .then((movieData) => {
        setMovie(movieData);
        console.log("Questa è quella che ci serve", movieData);
      });
  };

  useEffect(() => {
    getOmdbData();
  }, []);

  console.log(params.omdbId);
  return <div className="text-light">{movie ? <h1>{movie.title}</h1> : <div>Loading...</div>}</div>;
};

export default Detailts;
