const TopBar = () => {
  return (
    <div className="container-fluid d-flex justify-content-between bg-body" bg="dark" data-bs-theme="dark">
      <div className="d-flex align-items-center text-white my-2">
        <h1>
          Movies
          <div className="text-light">
            {movie ? (
              <h1>{movie.Title}</h1>
