import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import OMDB_URL from "../helpers/OmbdUrl";

const Detailts = () => {
  const params = useParams();
  const [movie, setMovie] = useState(null);

  //prendere la key 2491cb2f

  const getOmdbData = () => {
    // fetch(OMDB_URL + "&i=" + params.omdbId)
    console.log(params.omdbID);
    fetch(OMDB_URL + "&i=" + "tt3371366")
      .then((resp) => {
        if (resp.ok) {
          return resp.json();
        }
      })
      .then((movieData) => {
        setMovie(movieData);
        console.log("Questa è quella che ci serve", movieData);
      });
  };

  useEffect(() => {
    getOmdbData();
  }, []);

  console.log(params.omdbID);
  return (
    <div className="text-light">
      <img src={movie.Poster} alt="poster" />
      {movie ? (
        <h1>
          TITOLO FILM: <strong>{movie.Title}</strong>
        </h1>
      ) : (
        <div>
          <h2>Loading...</h2>
        </div>
      )}
    </div>
  );
};

export default Detailts;
